import React from 'react';
import { useParams } from 'react-router-dom';
import './PostPage.css';

const posts = [
  {
    id: 1,
    title: 'Fashion Trends',
    content: `
      <h2>Summer Trends</h2>
      <p>Explore the vibrant and colorful fashion trends for summer, including lightweight fabrics, bold prints, and breezy styles that keep you cool and chic.</p>
      <h2>Winter Wear</h2>
      <p>Stay warm and stylish with winter wear essentials. Discover cozy knitwear, fashionable coats, and layering techniques to keep you comfortable and trendy.</p>
      <h2>Spring Highlights</h2>
      <p>Embrace the freshness of spring with floral patterns, pastel colors, and lightweight jackets. Learn how to transition your wardrobe seamlessly from winter to spring.</p>
    `,
    image: '/assets/images/fashion.jpg'
  },
  {
    id: 2,
    title: 'Beauty Trends',
    content: `
      <h2>Top Skincare Products</h2>
      <p>Discover the top skincare products that will keep your skin glowing and healthy. From cleansers to moisturizers, find the best products for your skincare routine.</p>
      <h2>Makeup Trends</h2>
      <p>Stay updated with the latest makeup trends. Learn about bold eye makeup, natural looks, and the best products to achieve these trends.</p>
      <h2>Haircare Products</h2>
      <p>Find out which haircare products are must-haves for maintaining healthy and beautiful hair. From shampoos to styling products, discover what works best for your hair type.</p>
    `,
    image: '/assets/images/beauty.jpg'
  },
  {
    id: 3,
    title: 'Lifestyle',
    content: `
      <h2>Healthy Living</h2>
      <p>Learn about the best practices for maintaining a healthy lifestyle. From balanced diets to regular exercise, discover tips to keep you in top shape.</p>
      <h2>Travel Destinations</h2>
      <p>Explore the most beautiful and exciting travel destinations around the world. Get tips on where to go, what to see, and how to make the most of your travels.</p>
      <h2>Home Decor</h2>
      <p>Discover the latest trends in home decor. From minimalist designs to cozy interiors, get inspired to decorate your home in style.</p>
    `,
    image: '/assets/images/lifestyle.jpg'
  }
  // Add more posts if needed
];

const PostPage = () => {
  const { id } = useParams();
  const post = posts.find(p => p.id === parseInt(id));

  if (!post) {
    return <div>Post not found</div>;
  }

  return (
    <div className="post-page">
      <h2>{post.title}</h2>
      <img src={post.image} alt={post.title} className="post-image" />
      <div dangerouslySetInnerHTML={{ __html: post.content }} />
    </div>
  );
};

export default PostPage;
