import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HomePage from './components/HomePage';
import PostPage from './components/PostPage';
import NewPostForm from './components/NewPostForm';
import Header from './components/Header';
import Footer from './components/Footer';
import FashionPage from './components/FashionPage';
import BeautyPage from './components/BeautyPage';
import LifestylePage from './components/LifestylePage';
import './App.css';

function App() {
  return (
    <Router>
      <div className="app">
        <Header />
        <main>
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/fashion" element={<FashionPage />} />
            <Route path="/beauty" element={<BeautyPage />} />
            <Route path="/lifestyle" element={<LifestylePage />} />
            <Route path="/community" element={<div className="page">Community Page</div>} />
            <Route path="/personal-engagement" element={<div className="page">Personal Engagement Page</div>} />
            <Route path="/post/:id" element={<PostPage />} />
            <Route path="/new" element={<NewPostForm />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
