import React from 'react';
import { useParams } from 'react-router-dom';
import './PostPage.css';

const PostPage = () => {
  const { id } = useParams();
  const post = { id: '1', title: 'First Post', content: 'This is the first post content', image: 'sample-image1.jpg' }; // Placeholder

  return (
    <div className="post-page">
      <h2>{post.title}</h2>
      <img src={`./assets/images/${post.image}`} alt={post.title} className="post-page-image" />
      <p>{post.content}</p>
    </div>
  );
};

export default PostPage;
